$(function(){var $gnb=$('#gnb > li');var $sub=$('.sub_menu');var headerMin=$('#gnb').height();var headerMax=headerMin+$sub.innerHeight();var speed=200;var flag=false;$sub.hide();$gnb.mouseenter(function(){if(!flag){$('#gnb').stop().animate({height:headerMax},speed,function(){$sub.stop().fadeIn(speed)});flag=true}$(this).find('#newGbn > li').addClass('on');$(this).find('$sub').addClass('on')});$gnb.mouseleave(function(){$(this).find('#gnb > li').removeClass('on');$(this).find('$sub').removeClass('on')});$('#gnb').mouseleave(function(){$sub.stop().fadeOut(speed,function(){$('#gnb').stop().animate({height:headerMin},speed)});flag=false})});